# 新增 金额变更记录表 [2022-02-25] [zf]
CREATE TABLE `yz_money_change_record` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT ''id'',
  `field_key` varchar(100) NOT NULL COMMENT ''变更字段'',
  `field_val` varchar(255) NOT NULL COMMENT ''变更字段介绍'',
  `mytable_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT ''变更表名'',
  `mytype` smallint(5) NOT NULL COMMENT ''变更类型（手动，业务变更）'',
  `mybefore` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT ''变更前'',
  `change_value` varchar(255) NOT NULL COMMENT ''变更值'',
  `myfunction_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT ''函数名'',
  `remarks` varchar(500) NOT NULL COMMENT ''备注'',
  `par_val` longblob NOT NULL COMMENT ''请求参数'',
  `result` smallint(5) NOT NULL COMMENT ''执行结果'',
  `sel_key` varchar(255) NOT NULL COMMENT ''关联查询 字段'',
  `sel_val` varchar(255) NOT NULL COMMENT ''关联查询 字段值'',
  `create_date` datetime NOT NULL COMMENT ''创建时间'',
  `update_date` datetime NOT NULL COMMENT ''最近修改时间'',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT=''金额变更记录表'';



